mapper.select:
SELECT , coupon_info.coupon_name FROM coupon_user AS coupon_user LEFT JOIN coupon_info AS coupon_info ON coupon_user.coupon_id = coupon_info.id
AND coupon_user.del_flag = '0'
AND coupon_user.`status`=#{query.status}
AND coupon_user.`user_id`=#{query.userId}

SELECT
FROM coupon_user AS coupon_user
LEFT JOIN coupon_info AS coupon_info ON coupon_user.`coupon_id` = coupon_info.`id` LEFT JOIN coupon_goods AS coupon_goods ON coupon_info.`id` = coupon_goods.`coupon_id`
AND coupon_user.del_flag = '0'
AND coupon_user.`user_id` =#{query.userId}
AND coupon_user.`status` ='0' AND coupon_user.`validat_time` > now()
AND coupon_user.`status` ='1'
AND (coupon_user.`status` ='2' OR (coupon_user.`status` ='0' AND now()>coupon_user.`validat_time`) )
AND coupon_user.`status` =#{query.status}
AND (coupon_info.use_range = '1' OR (coupon_info.use_range = '2' AND coupon_goods.spu_id IN
#{item}
))

SELECT c.id AS id, COUNT(cr.id) AS userReceiveCount FROM coupon_info c LEFT JOIN coupon_user cr ON c.id = cr.coupon_id WHERE c.id IN
#{item}
AND c.del_flag = '0' AND c.`receive_ended_at` > now() AND now()> c.`receive_started_at` GROUP BY c.id;
